
import createElement from 'deck-of-cards/lib/createElement'

import animationFrames from 'deck-of-cards/lib/animationFrames'
import ease from 'deck-of-cards/lib/ease'
import bysuit from 'deck-of-cards/lib/modules/bysuit'
import fan from 'deck-of-cards/lib/modules/fan'
import intro from 'deck-of-cards/lib/modules/intro'
import poker from 'deck-of-cards/lib/modules/poker'
import shuffle from 'deck-of-cards/lib/modules/shuffle'
import sort from 'deck-of-cards/lib/modules/sort'
import flip from 'deck-of-cards/lib/modules/flip'

import observable from 'deck-of-cards/lib/observable'
import queue from 'deck-of-cards/lib/queue'
import prefix from 'deck-of-cards/lib/prefix'
import translate from 'deck-of-cards/lib/translate'

import Card from 'deck-of-cards/lib/card'

export default function Deck (jokers) {
  // init cards array
  var cards = new Array(jokers ? 55 : 52)

  var $el = createElement('div')
  var self = observable({mount, unmount, cards, $el})
  var $root

  var modules = Deck.modules
  var module

  // make queueable
  queue(self)

  // load modules
  for (module in modules) {
    addModule(modules[module])
  }

  // add class
  $el.classList.add('deck')

  var card

  // create cards
  for (var i = cards.length; i; i--) {
    card = cards[i - 1] = Card(i - 1)
    card.setSide('back')
    card.mount($el)
  }

  return self

  function mount (root) {
    // mount deck to root
    $root = root
    $root.appendChild($el)
  }

  function unmount () {
    // unmount deck from root
    $root.removeChild($el)
  }

  function addModule (module) {
    module.deck && module.deck(self)
  }
}
Deck.animationFrames = animationFrames
Deck.ease = ease
Deck.modules = {bysuit, fan, intro, poker, shuffle, sort, flip}
Deck.Card = Card
Deck.prefix = prefix
Deck.translate = translate
